import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import Home from './pages/Home';
import SujetsThese from './pages/SujetsThese';
import Login from './pages/Login';
import LoginProfesseur from './pages/LoginProfesseur'; // Ajouté
import Register from './pages/Register';
import Candidature from './pages/Candidature';
import SuiviCandidature from './pages/SuiviCandidature';
import EquipesRecherche from './pages/EquipesRecherche';
import Encadrants from './pages/Encadrants';
import Doctorants from './pages/Doctorants';
import EspaceProfesseur from './pages/EspaceProfesseur'; // Ajouté
import EspaceChefEquipe from './pages/EspaceChefEquipe'; // Ajouté
import EspaceDirecteurCEDoc from './pages/EspaceDirecteurCEDoc'

function App() {
  return (
    <BrowserRouter>
      <div style={{ minHeight: '100vh', background: '#f8f9fa' }}>
        <main style={{ width: '100%', margin: '0', padding: '0' }}>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/sujets-these" element={<SujetsThese />} />
            <Route path="/login" element={<Login />} />
            <Route path="/login-professeur" element={<LoginProfesseur />} /> {/* Ajouté */}
            <Route path="/register" element={<Register />} />
            <Route path="/candidature" element={<Candidature />} />
            <Route path="/suivi-candidature" element={<SuiviCandidature />} />
            <Route path="/equipes-recherche" element={<EquipesRecherche />} />
            <Route path="/encadrants" element={<Encadrants />} />
            <Route path="/doctorants" element={<Doctorants />} />
            <Route path="/espace-professeur" element={<EspaceProfesseur />} /> {/* Ajouté */}
            <Route path="/espace-chef-equipe" element={<EspaceChefEquipe />} /> {/* Ajouté */}
             <Route path="/espace-directeur-cedoc" element={<EspaceDirecteurCEDoc />} />
            
            {/* Route de fallback pour les pages non trouvées */}
            <Route path="*" element={
              <div className="min-h-screen flex items-center justify-center">
                <div className="text-center">
                  <h1 className="text-4xl font-bold mb-4">404 - Page non trouvée</h1>
                  <p className="text-xl mb-8">La page que vous cherchez n'existe pas ou a été déplacée</p>
                  <Link 
                    to="/" 
                    className="px-6 py-3 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                  >
                    Retour à l'accueil
                  </Link>
                </div>
              </div>
            } />
          </Routes>
        </main>
      </div>
    </BrowserRouter>
  );
}

export default App;
